mapper.select:
SELECT COUNT(*) FROM course_order o LEFT JOIN users u ON o.user_id = u.user_id LEFT JOIN course c ON o.course_id = c.id WHERE 1=1
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR c.name LIKE CONCAT('%', #{keyword}, '%') OR o.order_no LIKE CONCAT('%', #{keyword}, '%') )
AND o.status = #{status}

SELECT o.*, u.username, c.name AS courseName FROM course_order o LEFT JOIN users u ON o.user_id = u.user_id LEFT JOIN course c ON o.course_id = c.id WHERE 1=1
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR c.name LIKE CONCAT('%', #{keyword}, '%') OR o.order_no LIKE CONCAT('%', #{keyword}, '%') )
AND o.status = #{status}
ORDER BY o.created_at DESC LIMIT #{limit} OFFSET #{offset}
